INSERT INTO `berpredict_sum` (`predict_id`, `predict_numb`, `predict_name`, `predict_description`, `predict_pin`, `date_create`) VALUES
(82, 40, 'พลังอำนาจมหัศจรรย์ส่งเสริมความสำเร็จ', 'ฉลาดหลักแหลมและเป็นคนพูดเก่ง มักเอาตัวรอดเก่ง มีไหวพริบดี เป็นนักพูดนักเจรจาได้ดี เหมาะที่จะทำงานเกี่ยวกับ เลขา ประสานงานต่างๆ นักบัญชี นักข่าว นักสืบ นักเจรจาต่อรอง ชอบลองของใหม่ ชอบบุกเบิกสิ่งใหม่ ของทันสมัย ทำอะไรแหวกแนวสร้างความแปลกใจให้แก่สังคม ค้นหา', 'yes', '2019-08-13 16:36:02'),
(84, 23, 'พลังมหาเสน่ห์เมตตามหานิยม', 'เลขคู่นี้เป็นเลขที่เสน่ห์แรงมากๆแรงแบบฉุดไม่อยู่ เหมาะสำหรับคนที่โสดและต้องการคู่ หรือไร้คู่มานาน เลขคู่นี้สามารถช่วยคุณได้ดีเลยทีเดียว จากคนที่ดูไร้เสน่ห์ หรือ ดูจืดๆ หากเอามาใช้เลขคู่นี้จะทำให้ดูมีเสน่ห์ต่อผู้พบเห็น มีคนอุปถัมป์ค้ำชูเป็นที่รักของคนทั่วไป', 'no', '2019-08-13 17:05:49'),
(85, 25, 'พลังการสนับสนุนค้ำจุน', '	คุณสร้างเนื้อสร้างตัวได้เร็ว หาเงินเก่ง บุคลิกเป็นผู้ใหญ่ ปัญญาดี ใฝ่รู้ เข้ากับผู้ใหญ่ได้ดี เลขนี้เป็นเลขนารีอุปถัมภ์ ผู้ชายใช้ได้ดี จะได้รับความร่วมมือเกื้อหนุนจากสตรีเป็นอย่างดีเยี่ยม ตรงข้าม ถ้าผู้หญิงใช้จะต้องกลับเป็นฝ่ายเอาอกเอาใจผู้ชายแต่เลขคู่นี้ทำให้คู่รักหลายๆคู่อยู่กันยืดมานักต่อนัก แรกๆอาจขัดแย้งกันบ้าง แต่ต่อไปจะราบรื่น เข้าใจและเห็นใจกันเป็นอย่างดี ทั้งนี้ที่กล่าวไปเป็นการวิเคราะห์จากตำราหลายๆเล่ม แต่มีส่วนเสริมเล็กๆจากประสบการณ์ในการเปลี่ยนเบอร์ให้สุภาพสตรีหลายคนที่เบอร์เดิมมีเลขนี้ปนอยู่ มักจะเป็นคนช่างเอาอกเอาใจผู้ชาย ส่งเสริมเรื่องรักโรแมนติกให้รู้สึกกระชุ่มกระชวย เลขนี้จึงเหมาะกับบุรุษ หรือสตรีที่มีครอบครัวแล้ว', 'no', '2019-08-13 17:05:49'),
(86, 24, 'พลังแห่งความสำเร็จ สมบูรณ์พูลสุข', 'เป็นคนมีเสน่ห์เมตตามหานิยมสูง ใครเห็นใครก็รัก ใครเห็นก็ชื่นชม มีชีวิตสุขสบาย มีงานมีเงินเข้ามากขึ้น จิตใจดี ชอบเอื้อเฟื้อเผื่อแผ่ มีเพื่อนรายล้อมมากมาย และได้รับความช่วยเหลือเป็นอย่างดี หาเงินหาทองเก่ง เจรจาดี ปากหวาน หว่านล้อมคนได้ดี มีโชคเกี่ยวกับเงินทอ', 'no', '2019-08-13 17:05:49'),
(87, 36, 'พลังแห่งความรักความอบอุ่น เงินทองไม่ขาดมือ', 'เลขนี้ใช้ดีๆจะรวย เป็นบุคคลที่เด็ดเดี่ยวกล้าหาญ และน่ารักอ่อนหวานในคนเดียวกัน เป็นเลขพลังเสน่ห์และการเงิน เป็นคนมีเสน่ห์ ได้รับการสนับสนุนจากคนรอบข้าง ขยันหาเงินเก่งฉลาดหารายได้ มั่นใจในตัวเองสูง เป็นคนรักศักดิ์ศรี ไม่ง้อใคร ชอบเดินทางต่างถิ่น มีฐานะมั่นคง', 'yes', '2019-08-13 17:05:49'),
(88, 32, 'พลังมหาเสน่ห์เมตตามหานิยม', 'เลขคู่นี้เป็นเลขที่เสน่ห์แรงมากๆแรงแบบฉุดไม่อยู่ เหมาะสำหรับคนที่โสดและต้องการคู่ หรือไร้คู่มานาน เลขคู่นี้สามารถช่วยคุณได้ดีเลยทีเดียว จากคนที่ดูไร้เสน่ห์ หรือ ดูจืดๆ หากเอามาใช้เลขคู่นี้จะทำให้ดูมีเสน่ห์ต่อผู้พบเห็น มีคนอุปถัมป์ค้ำชูเป็นที่รักของคนทั่วไ', 'yes', '2019-08-23 09:32:59'),
(89, 41, 'พลังมหาจักรวาล ความรุ่งโรจน์สำเร็จ', 'เป็นกำลังดาวจักรพรรดิ จึงทำเป็นคนเข้มแข็ง มีพลังกล้าแข็ง ส่งเสริมให้ชีวิตประสบความสำเร็จอย่างดีเยี่ยม ทำให้เป็นคนมีความคิดสุขุมลุ่มลึก เป็นคนชอบศึกษาค้นคว้า มีความยุติธรรมเป็นหลักประจำใจ ถ้าเป็นเด็กจะเรียนเก่ง ผู้ใหญ่มักจะให้การอุปถัมภ์ เป็นคนที่มีความทะเ', 'yes', '2019-09-30 17:11:03'),
(90, 42, 'พลังแห่งความสำเร็จ สมบูรณ์พูลสุข', 'เป็นคนมีเสน่ห์เมตตามหานิยมสูง ใครเห็นใครก็รัก ใครเห็นก็ชื่นชม มีชีวิตสุขสบาย มีงานมีเงินเข้ามากขึ้น จิตใจดี ชอบเอื้อเฟื้อเผื่อแผ่ มีเพื่อนรายล้อมมากมาย และได้รับความช่วยเหลือเป็นอย่างดี หาเงินหาทองเก่ง เจรจาดี ปากหวาน หว่านล้อมคนได้ดี มีโชคเกี่ยวกับเงินทอ', 'yes', '2019-09-30 17:11:18'),
(91, 44, 'พลังส่งเสริมความสำเร็จ การเจรจาเป็นเลิศ', 'คุณเป็นคนฉลาด หัวไว ทันสมัย ชอบทดลองอะไรแปลกๆใหม่ๆ ชอบล้วงความลับ มีความสมารถในการสืบเสาะ ไหวพริบดี ช่างคิดช่างวางแผนบุคลิกเป็นคนหูไว ตาไว คล่องแคล่ว กระฉับกระเฉง ทันโลก ทันเกม ทันคน ชอบการเคลื่อนไหวและการเปลี่ยนแปลง ไม่ชอบอยู่นิ่งๆถนัดด้านงานสื่อสาร งานพ', 'yes', '2019-09-30 17:11:28'),
(92, 45, 'พลังแห่งความสำเร็จ มากทรัพย์ร่ำรวยมรดก', 'การรวมตัวของพลังปัญญาทางโลก และพลังปัญญาเหนือโลก ผู้ใดได้ใช้เลขชุดนี้จะมีพรสวรรค์ในด้านวิชาการ นำเสนอผลงานใดๆมักมีเหตุผลที่น่าทึ่งรองรับเสมอจึงไม่ค่อยถูกปฎิเสธจากผู้หลักผู้ใหญ่ สามารถบริหารจัดการและลำดับความคิดได้ดีเยี่ยม ทำธุรกิจก้ได้ รับราชการก้ดี เป็นเ', 'yes', '2019-09-30 17:48:17'),
(93, 46, 'พลังแห่งโชค วาจาเป็นทรัพย์ ร่ำรวยเงินทอง', 'เป็นคนมีเสน่ห์ เจรจาอ่อนหวานน่ารัก ใครเข้ามาคุยด้วยมักติดกับ หลงไหลในเสน่ห์ ช่างพูดช่างเจรจรา เป็นคนอารมณ์ดี สนุกสนานรักสวยรักงาม โรแมนติก ขี้เอาอกเอาใจมักเข้ากับคนได้ง่าย จึกเป็นที่รักของผู้คน เลขคู่นี้จึงมีแต่คนรัก ได้ดีเพราะปาก ค้าขายก็ดี พูดเก่งขายเก่', 'yes', '2019-09-30 17:48:29'),
(94, 50, 'พลังความสำเร็จที่ยิ่งใหญ่ มั่งคั่งอย่างมั่นคง', 'เป็นคนมีจิตใจดี ใจบุญสุนทาน รักความยุติธรรม ชอบศีกษาด้านศาสนาปรัชญา มีเหตุผลในการตัดสินใจด้วยการพิจารณาของตน คิดเองทำเองไม่ชอบพึ่งใคร ชอบท่องเที่ยวต่างแดน มักมีเพื่อนเป็นชาวต่างชาติ มีโชคในต่างแดน ชอบคบหากับคนที่อาวุโสกว่า เพราะมีความคิดที่เป็นผู้ใหญ่ เป็', 'yes', '2019-10-01 09:10:59'),
(95, 51, 'พลังความสมบูรณ์ พูลสุข', 'จัดเป็นหมายเลขที่โชคดีมากจะได้รับพรประเสริฐและสามารถนำความผาสุขเอนกอนันต์มาสู่คนทั้งปวงมีชะตารุ่งโรจน์สดใสดี เข้าหาผู้หลักผู้ใหญ่มักได้รับความเมตตา เป็นคนอ่อนน้อม รักความยุติธรรม สติปัญญาดี ดวงดี มีเหตุผล มักมีหน้าที่การงานดี ได้รับความเอ็นดูรักใคร่จากคนร', 'yes', '2019-10-01 09:11:12'),
(96, 54, 'พลังแห่งความสำเร็จ มากทรัพย์ร่ำรวยมรดก', 'การรวมตัวของพลังปัญญาทางโลก และพลังปัญญาเหนือโลก ผู้ใดได้ใช้เลขชุดนี้จะมีพรสวรรค์ในด้านวิชาการ นำเสนอผลงานใดๆมักมีเหตุผลที่น่าทึ่งรองรับเสมอจึงไม่ค่อยถูกปฎิเสธจากผู้หลักผู้ใหญ่ สามารถบริหารจัดการและลำดับความคิดได้ดีเยี่ยม ทำธุรกิจก้ได้ รับราชการก้ดี เป็นเ', 'yes', '2019-10-01 09:11:28'),
(97, 55, 'พลังความสุขสมหวังเจริญก้าวหน้า', 'พลังปัญญาบริสุทธ์ ผู้ใดได้ครอบครองหมายเลขนี้มักเป็นคนใฝ่ดี รักความยุติธรรม อบอุ่น อ่อนโยน ใจดี ใจเย็น เป็นคนมีสมาธิ มีสติในการครองตนอย่างดีเยี่ยม ชีวิตมักประสบความสำเร็จ งานดี เงินดี เป็นคนมีความสุขได้จากภายในจิตใจตนเอง รู้เท่าทันความคิดตน สมถะ ชีวิตโดยรว', 'yes', '2019-10-01 09:11:42'),
(98, 14, 'พลังแห่งสติปัญญาไหวพริบและมงคล', 'เป็นกำลังดาวจักรพรรดิ จึงทำเป็นคนเข้มแข็ง มีพลังกล้าแข็ง ส่งเสริมให้ชีวิตประสบความสำเร็จอย่างดีเยี่ยม ทำให้เป็นคนมีความคิดสุขุมลุ่มลึก เป็นคนชอบศึกษาค้นคว้า มีความยุติธรรมเป็นหลักประจำใจ ถ้าเป็นเด็กจะเรียนเก่ง ผู้ใหญ่มักจะให้การอุปถัมภ์ เป็นคนที่มีความทะเ', 'no', '2019-10-01 09:14:34'),
(99, 43, 'พลังแห่งสติปัญญา ความรอบรู้', 'หัวรุนแรง ช่างคิดช่างจดจำ ถนัดใช้คำพูดเชือดเฉือนหัวใจผู้อื่น มีชั้นเชิง มีความมุ่งมั่น มีความเฉลียวฉลาดในการประกอบอาชีพเลี้ยงปากท้อง เป็นคนไม่อ้อมค้อมปากว่ามือถึงชอบงานท้าทายให้ระวังจะมีศัตรูเพราะปาก ชะตามีความเสี่ยงจะเกิดอุบัติเหตุ มีเคราะห์ อาจเป็นโรคจิ', 'no', '2019-10-01 09:16:11'),
(103, 15, 'พลังแห่งเสน่ห์ อุปถัมภ์ค้ำชู', 'จัดเป็นหมายเลขที่โชคดีมากจะได้รับพรประเสริฐและสามารถนำความผาสุขเอนกอนันต์มาสู่คนทั้งปวงมีชะตารุ่งโรจน์สดใสดี เข้าหาผู้หลักผู้ใหญ่มักได้รับความเมตตา เป็นคนอ่อนน้อม รักความยุติธรรม สติปัญญาดี ดวงดี มีเหตุผล มักมีหน้าที่การงานดี ได้รับความเอ็นดูรักใคร่จากคนร', 'no', '2019-10-03 16:49:46'),
(104, 19, 'พลังแห่งความสำเร็จจากผู้อุปถัมภ์', 'มีบุคลิกภาพน่าเชื่อถือ รักเกียรติ มีความทะเยอทะยาน มีความคิดสร้างสรรค์  สามารถผ่านปัญหาและอุปสรรคไปได้บ่อยครั้ง มีโอกาสได้อยู่ในวงสังคมชั้นสูง หรือได้พบผู้สูงศักดิ์บ่อย มีชะตาที่เหมาะกับการติดต่อกับต่างประเทศ ไปอยู่ที่ไหนมักเป็นที่รู้จักอย่างรวดเร็วเข้าสั', 'no', '2019-10-03 16:53:08'),
(105, 34, 'พลังแห่งสติปัญญา ความรอบรู้', 'หัวรุนแรง ช่างคิดช่างจดจำ ถนัดใช้คำพูดเชือดเฉือนหัวใจผู้อื่น มีชั้นเชิง มีความมุ่งมั่น มีความเฉลียวฉลาดในการประกอบอาชีพเลี้ยงปากท้อง เป็นคนไม่อ้อมค้อมปากว่ามือถึงชอบงานท้าทายให้ระวังจะมีศัตรูเพราะปาก ชะตามีความเสี่ยงจะเกิดอุบัติเหตุ มีเคราะห์ อาจเป็นโรคจิต', 'no', '2019-10-03 16:56:01'),
(106, 5, 'พลังแห่งปัญญาและความสามารถ', 'เป็นคนมีจิตใจดี ใจบุญสุนทาน รักความยุติธรรม ชอบศีกษาด้านศาสนาปรัชญา มีเหตุผลในการตัดสินใจด้วยการพิจารณาของตน คิดเองทำเองไม่ชอบพึ่งใคร ชอบท่องเที่ยวต่างแดน มักมีเพื่อนเป็นชาวต่างชาติ มีโชคในต่างแดน ชอบคบหากับคนที่อาวุโสกว่า เพราะมีความคิดที่เป็นผู้ใหญ่ เป็', 'no', '2019-10-03 16:56:22'),
(107, 56, 'พลังความรุ่งเรืองสุขสมหวังทั้งความรักและเงินตรา', 'เลขคู่แห่งบุญญาบารมี วาสนา และปัญญา เรียกว่า &#34;คู่ทรัพย์ คู่โชค&#34; ผู้ใดได้ครอบครองเลขนี้จะเข้าใจชีวิตจากทั้งทางโลกและทางธรรม เรียนเก่ง ทำงานเก่ง ทำสิ่งใดมักบรรลุผลตามเป้าหมาย เป็นบุคคลที่มีเสน่ห์ เฉลียวฉลาด เป็นที่รักใครของคนรอบข้าง ประสบความสำเร็จใ', 'yes', '2019-10-03 16:58:27'),
(108, 65, 'พลังความรุ่งเรืองสุขสมหวังทั้งความรักและเงินตรา', 'เลขคู่แห่งบุญญาบารมี วาสนา และปัญญา เรียกว่า &#34;คู่ทรัพย์ คู่โชค&#34; ผู้ใดได้ครอบครองเลขนี้จะเข้าใจชีวิตจากทั้งทางโลกและทางธรรม เรียนเก่ง ทำงานเก่ง ทำสิ่งใดมักบรรลุผลตามเป้าหมาย เป็นบุคคลที่มีเสน่ห์ เฉลียวฉลาด เป็นที่รักใครของคนรอบข้าง ประสบความสำเร็จใ', 'yes', '2019-10-03 16:58:32'),
(109, 59, 'พลังความรุ่งเรืองก้าวหน้า ยอดรหัสแห่งชีวิต', 'มีโชคลาภบ่อยๆ สังหรณ์แม่น ฝันแม่น ชีวิตมักมีเรื่องเกี่ยวพันกับเรื่องราวโบราณ หรือเทคโนโลยีสมัยใหม่ มีสิ่งศักดิ์สิทธิ์คุ้มครองดีมาก ผู้ใหญ่ให้ความเมตตาเป็นอย่างมาก เป็นคนใจบุญ รักการให้รักการทำบุญ และมักจะเห็นผลทันตา โชคลาภเข้ามาแบบไม่ทันตั้งตัว สุขภาพดี ท', 'yes', '2019-10-03 17:01:47'),
(110, 95, 'พลังของสิ่งศักดิ์สิทธิ์คุ้มครอง', '', 'no', '2019-10-03 17:01:58'),
(111, 6, 'พลังแห่งโชค ร่ำรวยเงินทองพลังแห่งโชค', 'มีโชคเรื่องการเงิน และความเพ้อฝัน เป็นคนมีเสน่ห์ ดึงดูดใจ ให้คนชอบพอรักใคร่ เป็นบุคคลที่มีความโชคดีเกี่ยวกับเรื่องเงินๆทองๆ จะสามารถหามาได้โดยง่าย ศุภโชค โภคทรัพย์ เข้ากับคนได้ทุกระดับ จะมีคนช่วยเหลือสนับสนุน เป็นเลขแห่งศิลป์และความเพ้อฝัน รักสวยรักงาม อา', 'no', '2019-10-03 17:03:02'),
(112, 60, 'พลังแห่งศิลป์ มีโชคเรื่องการเงิน', 'มีโชคเรื่องการเงิน เป็นคนมีเสน่ห์ ดึงดูดใจ ให้คนชอบพอรักใคร่ เป็นบุคคลที่มีความโชคดีเกี่ยวกับเรื่องเงินๆทองๆ จะสามารถหามาได้โดยง่าย ศุภโชค โภคทรัพย์ เข้ากับคนได้ทุกระดับ จะมีคนช่วยเหลือสนับสนุน เป็นเลขแห่งศิลป์และความเพ้อฝัน รักสวยรักงาม อารมณ์ปราณีต ละเอ', 'yes', '2019-10-03 17:03:07'),
(116, 53, 'พลังแห่งผู้นำ อำนาจ บารมี', 'คุณเป็นคนมีวาสนาดีทีเดียว ได้รับการอุปถัมป์ค้ำชูจากทั้งผู้บังคับบัญชา และผู้ใต้บังคับบัญชา มีคนนับหน้าถือตามีอำนาจบารมี การเงินหมุนเวียนดีพอสมควร สามารถร่ำรวยได้ไม่ยากประสบความสำเร็จเพราะพลังงานเข้มแข็งในตัว ใจคอกว้างขวาง มีมาดดุดัน น่ายำเกรง และมีเสน่ห์ต', 'yes', '2019-10-04 10:35:23'),
(118, 33, 'พลังแห่งความมานะพากเพียร', 'อารมณ์รุนแรง แบบนักรบ มีพละกำลังมาก แข็งแกร่งทั้งร่างกายและจิตใจ ขยันสู้งาน ทรหด อดทน ขาลุยของแท้และดั้งเดิม ชอบทำมากกว่าพูด กล้าได้กล้าเสีย ถึงลูกถึงคน วู่วาม มุทะลุ ดุดัน อารมณ์ร้อน ขี้หงุดหงิด ทำการงานรวดเร็ว ว่องไว พูดจาตรงไปตรงมา จริงจังทุกคำพูด อาจจ', 'no', '2019-10-04 17:37:46'),
(119, 63, 'พลังแห่งความรักความอบอุ่น เงินทองไม่ขาดมือ', 'เลขนี้ใช้ดีๆจะรวย เป็นบุคคลที่เด็ดเดี่ยวกล้าหาญ และน่ารักอ่อนหวานในคนเดียวกัน เป็นเลขพลังเสน่ห์และการเงิน เป็นคนมีเสน่ห์ ได้รับการสนับสนุนจากคนรอบข้าง ขยันหาเงินเก่งฉลาดหารายได้ มั่นใจในตัวเองสูง เป็นคนรักศักดิ์ศรี ไม่ง้อใคร ชอบเดินทางต่างถิ่น มีฐานะมั่นค', 'yes', '2019-10-04 17:39:01'),
(120, 99, 'พลังความสมบูรณ์พูลสุข', 'ดวงดีมากๆ มีบารมี มีคนเคารพ เป็นคนมีสัมผัสพิเศษ ยิ่งทำบุญมากยิ่งทำให้มีโชคไม่คาดฝัน ร่ำรวยรวดเร็วไม่ว่าจะทำอะไรก็ดีไปหมด มีกมีลางสังหรณ์แม่นยำ มีสิ่งศักดิ์สิทธิ์คอยคุ้มครอง ประสบความสำเร็จและโชคลาภแบบปฏิหารอยู่เสมอ บางคนมีวาจาศักดิ์สิทธิ์พูดสิ่งใดมักเป็นจ', 'no', '2019-10-05 10:09:38'),
(121, 98, 'พลังอำนาจบารมี', 'มีอำนาจบารมี ใจกว้าง มีบารมีสั่งลูกน้องบริวาล หรือคุมคนได้ดี มีความเชื่อมั่นในตัวเองสูง มีสิ่งศักดิ์สิทธิ์คุ้มครอง ลางสังหรณ์แม่น เป็นคนไม่ยอมง้อใคร ชอบพึ่งพาตนเอง ดวงแข็งแคล้วคลาดปลอดภัย เป็นคนมีพลังจิตดีมีสัมผัสที่หกสามารถคาดการณ์ได้แม่นยำ ชอบเกี่ยวกับว', 'no', '2019-10-05 10:11:00'),
(122, 89, 'พลังอำนาจบารมี', 'มีอำนาจบารมี ใจกว้าง มีบารมีสั่งลูกน้องบริวาล หรือคุมคนได้ดี มีความเชื่อมั่นในตัวเองสูง มีสิ่งศักดิ์สิทธิ์คุ้มครอง ลางสังหรณ์แม่น เป็นคนไม่ยอมง้อใคร ชอบพึ่งพาตนเอง ดวงแข็งแคล้วคลาดปลอดภัย เป็นคนมีพลังจิตดีมีสัมผัสที่หกสามารถคาดการณ์ได้แม่นยำ ชอบเกี่ยวกับว', 'no', '2019-10-05 10:11:18'),
(123, 97, 'พลังความรุ่งโรจน์ วิสัยทัศน์ดี', 'มีบารมี มีวิชาความรู้พอตัว วิสัยทัศน์ดี มีไอเดียเก๋ๆ แปลกๆ ให้คนรอบข้างทึ่งอยู่เสมอ มีความสนใจในเรื่องเหนือธรรมชาติและเรื่องเทคโนโลยีความล้ำสมัยไปพร้อมๆกัน เจ้าตัวมีบุคลิกทะมัดทะแมง มีสไตล์โดดเด่นไม่เหมือนใคร มักคาดการณ์สิ่งต่างๆได้แม่นยำ เหมาะกับการลงทุน', 'no', '2019-10-05 10:15:06'),
(124, 1, 'พลังของนักสู้ เป็นคนที่มีความกล้าหาญ', 'เป็นคนมีความมั่นใจในตัวเองสูง มีความทะเยอทะยานดี ชอบของแปลกใหม่ ไม่ชอบซ้ำแบบใคร เป็นคนถือเกียรติ ไม่ชอบอยู่ใต้บังคับบัญชาของใคร ชอบทำอะไรที่แปลกแหวกแนวไม่เหมือนใคร ไม่ค่อยนุ่มนวล กล้าได้กล้าเสีย ชีวิตมักมีอุปสรรค จับงานอะไรมักจะเจอคู่แข่ง มีคนปองร้าย บริว', 'no', '2019-10-05 11:36:03'),
(125, 10, 'พลังของนักสู้ เป็นคนที่มีความกล้าหาญ', 'เป็นคนมีความมั่นใจในตัวเองสูง มีความทะเยอทะยานดี ชอบของแปลกใหม่ ไม่ชอบซ้ำแบบใคร เป็นคนถือเกียรติ ไม่ชอบอยู่ใต้บังคับบัญชาของใคร ชอบทำอะไรที่แปลกแหวกแนวไม่เหมือนใคร ไม่ค่อยนุ่มนวล กล้าได้กล้าเสีย ชีวิตมักมีอุปสรรค จับงานอะไรมักจะเจอคู่แข่ง มีคนปองร้าย บริว', 'no', '2019-10-05 11:38:08'),
(126, 96, 'พลังความรุ่งเรืองสุขสมหวังทั้งความรักและเงินตรา', 'เป็นเลขที่มีเสน่ห์เมตตามหานิยม มีรสนิยมดี รักเพื่อนฝูง เงินทองหาง่ายๆ มีกินมีใช้ตลอดไม่ขาดมือ มีโชคมีลาภแบบฟลุคๆ มีสิ่งศักดิ์สิทธิ์คุ้มครอง มีชื่อเสียงโดดเด่นใครๆมักรู้จักในวงสังคมเป็นที่รักของใครๆ มีเสนห์แรงต่อเพศตรงข้าม มีนิสัยร่าเริงเข้ากับใครง่ายๆ พูด', 'no', '2019-10-05 14:03:15'),
